mapper.select:
SELECT empNo, pwd, name
FROM employee
WHERE empNo = #{empNo}

mapper.update:
		UPDATE employee SET empNo = #{empNo}, name = #{name}, birth = #{birth}, tel = #{tel},
    		email = #{email}, addr = #{addr}, dCode = #{dCode}, pCode = #{pCode}, enterDate = #{enterDate},
    		exitDate = #{exitDate}, apDate = #{apDate}, memo = #{memo}
		WHERE employeeNum = #{employeeNum}
